INSERT INTO Magics (Name, Description)
VALUES
    ('Magie du contrôle de l''âme', 'Cette magie permet de prendre le contrôle d''un être vivant pour une durée plus ou moins longue, à condition que cet être ait une puissance magie au moins trois fois inférieure à celle de l''utilisateur. À cause de cette condition, cette magie est largement utilisée dans l''agriculture car il faudrait une puissance magique absolument démesurée pour être capable de prendre le contrôle d''une armée ou d''un général puissant.'),
    ('Magie de renforcement musculaire', 'Contrairement à son homologue de type support, cette magie n''affecte que son porteur et permet simplement de le rendre plus musclé. Les modifications sont d''ailleurs physiquement visibles sur son corps.'),
    ('Magie de téléportation', 'Cette magie permet de se déplacer spontanément d''un point A à un point B connu par la personne qui s''en sert.'),
    ('Magie du contrôle spatial', 'Une magie permettant de déplacer des objets dans l''espace sans la moindre contrainte autre qu''ils ne peuvent pas passer au travers d''autres objets. Elle ne permet également pas de modifier leur forme, uniquement de les déplacer.'),
    ('Magie du modelage', 'Cette magie permet de modifier la forme des choses selon l''imagination de son utilisateur.'),
    ('Magie de ciblage', 'Une magie très particulière, dont l''usage s''est perdu au fil du temps. Comme celle du contrôle de l''âme, elle permet de contrôler sa cible, mais seulement pour l''obliger à attaquer son porteur. Pour qu''elle prenne effet, son porteur doit avoir une puissance magique d''au moins 1,25 fois celle de la cible.'),
    ('Magie du vent', 'Cette magie permet de contrôler à une certaine échelle le vent. Grâce à elle on peut notamment créer de grands mouvements d''air pouvant infliger des dégâts à une cible ou simplement la repousser violemment..'),
    ('Magie de projection', 'Cette magie permet de donner un effet de projection à tout coup porté.'),
    ('Magie d''acier', 'Elle permet de transformer en acier tout ce que le porteur désire, tant que c''est en contact avec sa peau.'),
    ('Magie de théorie', 'Cette magie est à l''origine des plus grands théoriciens et autres scientifiques. Elle est très pratique pour exceller dans son domaine de compétence, quel qu''il soit, mais ne donne aucun pouvoir offensif.'),
    ('Magie de combustion', 'Une magie permettant de brûler de l''intérieur toute matière.'),
    ('Magie de l''arc d''ombre', 'Elle permet de créer un arc et des flèches d''ombre, qui sont capable de transporter certaines capacités du mage.'),
    ('Magie de l''épée de feu', 'Magie permettant de matérialiser une épée à partir de flammes.'),
    ('Magie de l''eau', 'Une magie qui permet de générer de l''eau spontanément.')
